import { commonApi } from "./commonApi"
import { serverUrl } from "./serverUrl"


//upload videos

export const uploadvideo = async(reqBody)=>{
          //make POST http request "http://localhost:4000/videos" to add videos to the json server return response to the add Component

          return await commonApi("POST",`${serverUrl}/videos`,reqBody)
}

//get all videos from json 
export const getAllVideos=async()=>{

          //make GET http request to "http://localhost:4000/videos" to get all videos from json-server return response to the view Component

          return await commonApi("GET",`${serverUrl}/videos`,"")
}


//get a video
export const getAVideos=async(id)=>{

          //make GET http request to "http://localhost:4000/videos" to get a videos from json-server return response to the view Component

          return await commonApi("GET",`${serverUrl}/videos${id}`,"")
}

//delete a video

export const deleteVideos=async(id)=>{

          //make DELETE http request to "http://localhost:4000/videos" to delete videos from json-server return response to the view Component

          return await commonApi("DELETE",`${serverUrl}/videos${id}`,{})
}
