import React, { useState } from 'react'
import { Button, Form, Modal } from 'react-bootstrap';





function Add() {

  const [video,setVideo]=useState({
    id:"",caption:"",url:"",embedLink:""
  })

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const getEmbedLink =(e)=>{

    const {value}=e.target
    if(value){
    const link=`https://www.youtube.com/embed/{${value.slice(-11)}`
    setVideo({...video,embedLink:link})
  }
  else{
    setVideo({...video,"embedLink":""});
  }
}

const handleupload= async()=>{
  const{id,caption,url,embedLink}=video
  if(!id||!caption||!url||!embedLink){
    
  }
}

// console.log(video);
  
  return (
    <>
    <div className="d-flex align-items-center">
      <h5>Upload Videos</h5>
      <button className='btn' onClick={handleShow}><i class="fa-solid fa-circle-plus fa-beat fa-2xl" style={{color:'black'}}></i></button>
    </div>



    <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
        
      >
        <Modal.Header closeButton>
          <Modal.Title>Upload Videos</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p style={{color:'black'}}>Fill the Following Fields</p>
          <Form>
          <Form.Group className="mb-3" controlId="formBasicEmail">
        
        <Form.Control type="text" placeholder="Enter Video Id " onChange={(e)=>setVideo({...video,id:e.target.value})}/>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        
        <Form.Control type="text" placeholder="Enter Video Title" onChange={(e)=>setVideo({...video,caption:e.target.value})}/>
      </Form.Group>


      <Form.Group className="mb-3" controlId="formBasicEmail">
        
        <Form.Control type="text" placeholder="Enter Video Image Url" onChange={(e)=>setVideo({...video,url:e.target.value})} />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        
        <Form.Control type="text" placeholder="Enter Video Url" onChange={(e)=>setVideo({...video,getEmbedLink:e.target.value})} />
      </Form.Group>


          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cancel
          </Button>
          <Button variant="primary">Upload</Button>
        </Modal.Footer>
      </Modal>

    </>
  )
}

export default Add
