import React, { useEffect, useState } from 'react'
import { Col, Row } from 'react-bootstrap'
import VideoCard from './VideoCard'
import { getAllVideos } from "../"



function View() {

  const[allvideos,setAllvideos]=useState([])
  // Fetch all videos when the component mounts.



  const getalluploadvideos= async ()=>{
    const {data} = await getAllVideos()
    setAllvideos(data)
  }
useEffect(()=>{
  getalluploadvideos()
},[])

  return (
    <>
    <Row>

      {

      allvideos.length>0?
      allvideos.map(video =>(
      <Col sm={12} md={6} lg={4} xl={3}>
        <VideoCard/>
      </Col>)): <p className='fw-bolder text-danger fs-5'>Nothing to Display</p>

}

    </Row>
    </>
  )
}

export default View
